import tkinter as tk
from tkinter.filedialog import askopenfilename, asksaveasfilename
import time
from datetime import datetime

def start_mouse_mover():
    minutes_to_repeat = minutes.get();
    to_x = 400
    to_y = 400
    x = 0
    y = 0
    step_x = (to_x-0)/100
    step_y = (to_y-0)/100
    for _ in range (100):
        x += step_x
        y += step_y
        window.event_generate('<Motion>', warp = True, x = x, y = y)
        time.sleep(0.1)
        window.update ()
    else:
        #Final position adjustment
        window.event_generate ('<Motion>', warp = True, x = to_x, y = to_y)
        txt_show.insert(tk.END, "Movement made at {}".format(datetime.now().time()) + "\n")
    
    window.after(minutes_to_repeat*60*1000, start_mouse_mover)

def clear_textarea():
    txt_show.delete('1.0', tk.END)

window = tk.Tk()
window.geometry("450x300")
window.title("My mouse mover")
window.rowconfigure(0, minsize=200, weight=1)
window.columnconfigure(1, minsize=200, weight=1)

txt_show = tk.Text(window)
fr_buttons = tk.Frame(window, relief=tk.RAISED, bd=2)

label = tk.Label(fr_buttons, text="Insert the minutes:")

minutes = tk.IntVar()
e = tk.Entry(fr_buttons, textvariable=minutes)
minutes.set(3)


img = """"""
btn_start_img = tk.PhotoImage(data=img)
btn_start = tk.Button(fr_buttons, text="Move mouse", image = btn_start_img, command = start_mouse_mover)

img = """"""
btn_clear_img = tk.PhotoImage(data=img)
btn_clear = tk.Button(fr_buttons, text="Clear", image = btn_clear_img, command=clear_textarea)

img = """"""
btn_quit_img = tk.PhotoImage(data=img)
btn_quit = tk.Button(fr_buttons, text="Quit", image = btn_quit_img, command=window.quit)

label.grid(row=0, column=0, sticky="ew")
e.grid(row=1, column=0, sticky="ew", padx=5, pady=5)
btn_start.grid(row=2, column=0, sticky="ew", padx=5, pady=5)
btn_clear.grid(row=3, column=0, sticky="ew", padx=5)
btn_quit.grid(row=4, column=0, sticky="ew", padx=5)

fr_buttons.grid(row=0, column=0, sticky="ns")
txt_show.grid(row=0, column=1, sticky="nsew")

window.mainloop()